package com.wdbyte.network.icp;

import javax.imageio.ImageIO;
import java.awt.*;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.util.Base64;

import lombok.extern.slf4j.Slf4j;

@Slf4j
public class CheckImageUtil {
    public static void main(String[] args) {
        String base64String = "";
        byte[] decodedBytes = Base64.getDecoder().decode(base64String);
        try (FileOutputStream fos = new FileOutputStream("image.png")) {
            fos.write(decodedBytes);
        } catch (IOException e) {
            e.printStackTrace();
        }
        changeImageGray(decodedBytes);
        checkImage(decodedBytes);
    }

    public static void changeImageGray(byte[] imgByte) {
        try {
            // 读取图片
            BufferedImage image = ImageIO.read(new ByteArrayInputStream(imgByte));
            // 获取图片的宽度和高度
            int width = image.getWidth();
            int height = image.getHeight();
            int[][] lightArray = new int[height][width];
            // 遍历图片的像素点，将其转换为黑白
            for (int y = 0; y < height; y++) {
                for (int x = 0; x < width; x++) {
                    // 获取像素点的颜色
                    Color color = new Color(image.getRGB(x, y));
                    // 计算灰度值
                    int gray = Double.valueOf((color.getRed() * 0.2126 + color.getGreen() * 0.7152 + color.getBlue() * 0.0722)).intValue();
                    // 将像素点的颜色设置为非黑即白
                    Color newColor = new Color(gray, gray, gray);
                    image.setRGB(x, y, newColor.getRGB());
                }
            }
            // 保存黑白图片
            File output = new File("gray.jpg");
            ImageIO.write(image, "jpg", output);
            log.info("验证码图片已保存为：{}" ,output.getAbsolutePath());
        } catch (Exception e) {
            e.printStackTrace();
        }
    }


    public static int checkImage(byte[] imgByte) {
        try {
            // 读取图片
            BufferedImage image = ImageIO.read(new ByteArrayInputStream(imgByte));
            // 获取图片的宽度和高度
            int width = image.getWidth();
            int height = image.getHeight();
            int[][] lightArray = new int[height][width];
            // 遍历图片的像素点，将其转换为黑白
            for (int y = 0; y < height; y++) {
                for (int x = 0; x < width; x++) {
                    // 获取像素点的颜色
                    Color color = new Color(image.getRGB(x, y));
                    // 计算灰度值
                    int gray = Double.valueOf((color.getRed() * 0.2126 + color.getGreen() * 0.7152 + color.getBlue() * 0.0722)).intValue();
                    if (gray > 100) {
                        gray = 255;
                    } else {
                        gray = 0;
                    }
                    lightArray[y][x] = gray;
                    // 将像素点的颜色设置为非黑即白
                    Color newColor = new Color(gray, gray, gray);
                    image.setRGB(x, y, newColor.getRGB());
                }
            }
            // 打印像素亮度
            //for (int[] ints : lightArray) {
            //    for (int anInt : ints) {
            //        System.out.print(anInt);
            //        System.out.print("\t");
            //    }
            //    System.out.println();
            //}

            // lightArray 存储了二值化后的图片像素信息
            // 扫描发现 255->0 变化的最大数量
            int maxChangeCount = 0;
            // maxChangeCount 对应的列值
            int index = 0;
            for (int w = 1; w < width; w++) {
                int changeCount = 0;
                for (int h = 0; h < height; h++) {
                    if (lightArray[h][w] == 0 && lightArray[h][w - 1] == 255) {
                        changeCount++;
                    }
                }
                if (changeCount > maxChangeCount) {
                    maxChangeCount = changeCount;
                    index = w;
                }
            }
            // 保存黑白图片
            File output = new File("output.jpg");
            ImageIO.write(image, "jpg", output);
            log.info("验证码图片已保存为：{}" ,output.getAbsolutePath());
            log.info("分析得到验证码坐标值：{}" , index);
            return index;
        } catch (Exception e) {
            e.printStackTrace();
        }
        return 0;
    }
}
